def on_on_overlap(sprite, otherSprite):
    otherSprite.destroy()
    info.change_score_by(3)
    sprite.destroy()
sprites.on_overlap(SpriteKind.projectile, SpriteKind.fireball, on_on_overlap)

def on_overlap_tile2(sprite, location):
    game.over(False, effects.confetti)
scene.on_overlap_tile(SpriteKind.player, myTiles.tile11, on_overlap_tile2)

def on_on_overlap2(sprite, otherSprite):
    sprite.vx = otherSprite.x - sprite.x
    sprite.vy = otherSprite.y - sprite.y
sprites.on_overlap(SpriteKind.player, SpriteKind.bumper, on_on_overlap2)

def on_overlap_tile3(sprite, location):
    global level
    level += 1
    startLevel()
scene.on_overlap_tile(SpriteKind.player, myTiles.tile12, on_overlap_tile3)

def on_on_overlap3(sprite, otherSprite):
    music.ba_ding.play()
    info.change_score_by(1)
    otherSprite.destroy(effects.disintegrate, 100)
sprites.on_overlap(SpriteKind.player, SpriteKind.coin, on_on_overlap3)

def on_player2_disconnected():
    global player2connected
    player2connected = False
    player2.destroy()
controller.player2.on_event(ControllerEvent.DISCONNECTED, on_player2_disconnected)

def on_on_overlap4(sprite, otherSprite):
    if sprite.y < otherSprite.y:
        sprite.ay = -150
sprites.on_overlap(SpriteKind.player, SpriteKind.player, on_on_overlap4)

def on_on_overlap5(sprite, otherSprite):
    info.change_life_by(-1)
    if sprite.y < otherSprite.y:
        sprite.vy = -150
sprites.on_overlap(SpriteKind.player, SpriteKind.fireball, on_on_overlap5)

