def on_on_overlap7(sprite, otherSprite):
    if sprite.y < otherSprite.y:
        sprite.set_velocity(0, -150)
        info.change_score_by(1)
        otherSprite.destroy()
    else:
        info.change_life_by(-1)
        otherSprite.destroy()
sprites.on_overlap(SpriteKind.player, SpriteKind.enemy, on_on_overlap7)

